const http = require('http');
const express = require('express');
const { send } = require('process');
const app = express();

app.get("/", function(req, res){
  res.sendFile("Home");
});

app.get("/sobre", function(req, res){
  res.send("Sobre");
});


app.listen(3000, function(){
    console.log("Servidor ON - http://localhost:3000/");
});
