import { useState } from 'react';
var http = require('http');
var fs = require('fs');
var url = require('url');

var pathPage = function(page){
      return learning-web + "/html/" + page + ".html";
};

var router = function(pathname){
    if(pathname && pathname != "/"){
        var exist = fileExists( pathPage(pathname) );
        return exist ? pathPage(pathname) : pathPage("erro");
    }
    return pathPage("index");
};

var fileExists = function(filePath){
    try{
        return fs.statSync(filePath).isFile();
    }catch (err){
        return false;
    }
};

var server = http.createServer(function (request, response) {
    var page = router( url.parse(request.url).pathname );
    fs.readFile(page, function(err, data){
        response.end(data);
    });
});

server.listen(3000, function () {
    console.log('Servidor rodando na porta 3000');
});

function Home(){
  return (
    <div>
      <h1>Home</h1>
      <Contador />
    </div>
  )
}

function Contador(){
  const [contador,setContador] = useState(1);

  function adicionarContador(){
    setContador(contador + 1);
  }
  return(
    <div>
      <div>{contador}</div>
      <button onClick={adicionarContador}>Adicionar</button>
      <div>Test</div>
    </div>
  )
}
export default Home
